import React from 'react';
import '../styles/Culture.css';

const Culture = () => {
  const culturalEvents = [
    {
      id: 1,
      name: 'Thaipusam',
      description: 'A vibrant Hindu festival celebrated at the Waterfall Temple',
      time: 'January/February',
      significance: 'Religious and cultural celebration with processions and rituals'
    },
    {
      id: 2,
      name: 'George Town Festival',
      description: 'Month-long celebration of arts, culture, and heritage',
      time: 'July/August',
      significance: 'Showcases local and international artistic performances'
    },
    {
      id: 3,
      name: 'Nine Emperor Gods Festival',
      description: 'Taoist celebration with vegetarian food and ceremonies',
      time: 'September/October',
      significance: 'Important festival for the Chinese community'
    },
    {
      id: 4,
      name: 'Penang Heritage Celebrations',
      description: 'Celebration of Penang\'s UNESCO World Heritage status',
      time: 'July',
      significance: 'Cultural performances, food festivals, and heritage activities'
    }
  ];

  return (
    <div className="culture-page">
      <div className="culture-hero">
        <h1>Cultural Heritage</h1>
        <p>Experience the rich multicultural heritage of Penang</p>
      </div>

      <div className="culture-grid">
        {culturalEvents.map(event => (
          <div key={event.id} className="culture-card">
            <div className="culture-content">
              <h3>{event.name}</h3>
              <p className="description">{event.description}</p>
              <div className="event-details">
                <p><strong>When:</strong> {event.time}</p>
                <p><strong>Significance:</strong> {event.significance}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Culture;
